const fixedHeaderWhenScrolling=()=>{const e=document.querySelector(".header");window.scrollY>0?e.classList.add("header--fixed"):e.classList.remove("header--fixed")},openCloseSidebar=()=>{const e=document.querySelector(".header__burger"),d=document.querySelector(".sidebar");e.addEventListener("click",(()=>{const o=d.classList.toggle("sidebar--open");e.classList.toggle("header__burger--open"),(e=>{document.body.style.overflow=e?"hidden":""})(o)}))};document.addEventListener("DOMContentLoaded",(()=>{window.addEventListener("scroll",(function(){fixedHeaderWhenScrolling()})),openCloseSidebar()}));